comments.map:
function(doc) {
  if(doc.type == 'comment') {
   emit(null, { jurisdiction_id: doc.jurisdiction_id, request_id: doc.service_request_id, comment: doc.comment });
  }
}

config.map:
function(doc) {
  if(doc.type == 'config') {
  emit(null, {access_token: doc.auth_values.access_token, app_id: doc.app_id, app_secret: doc.app_secret, environment: doc.environment, agency: doc.agency, module: doc.module});
}
}
